mapper.select:
SELECT id, telegram_id, username, first_name, last_name, language_code, is_active, created_at, updated_at
FROM user
WHERE id = #{id}

SELECT id, telegram_id, username, first_name, last_name, language_code, is_active, created_at, updated_at
FROM user
WHERE telegram_id = #{telegramId}

mapper.update:
UPDATE user
SET is_active = #{isActive}
WHERE id = #{id}
