mapper.select:
SELECT id, user_id, current_project_id, state, state_data, last_activity_at, created_at, updated_at
FROM user_session
WHERE user_id = #{userId}

mapper.update:
UPDATE user_session
SET current_project_id = #{currentProjectId},
    state = #{state},
    state_data = #{stateData}
WHERE user_id = #{userId}

UPDATE user_session
SET current_project_id = #{projectId}
WHERE user_id = #{userId}

UPDATE user_session
SET state = #{state},
    state_data = #{stateData}
WHERE user_id = #{userId}
